/** 4. Programa que imprima la suma de los cien primeros numeros
 */
public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int suma = 0 ;
		int contador = 1 ;
		while (contador <=100) {
			suma = suma + contador ;
			contador++ ;
		}
		System.out.println(suma);
	}

}
